import React, { Component } from 'react';

import { Route } from 'react-router-dom';

import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import CollectionPreview from '../../components/collection-preview/collection-preview.component';
import Collection from '../../components/collection/collection.component';

import { firestore, convertCollectionSnapshotToMap } from '../../firebase/firebase.util';



class ShopPage extends Component {

    componentDidMount() {
        const collectionRef = firestore.collection('collections');

        collectionRef.onSnapshot(async (snap) => {
            console.log('ohh snappy snappy');
            await console.log(convertCollectionSnapshotToMap(snap));
        });
    }

    render() {
        return(
        <div className='shop-page'>
            <Route exact={true} path={`${this.props.match.path}`} component={CollectionOverview} />
            <Route path={`${this.props.match.path}/:collectionId`} component={Collection} />
        </div>
        );
    }
}

export default ShopPage;
