export const addItemToCart = (cartItems, itemToadd) => {
  const existingItem = cartItems.find(cartItem => cartItem.id === itemToadd.id);

  if (existingItem) {
    return cartItems.map(cartItem => {
      return cartItem.id == itemToadd.id
        ? { ...cartItem, quantity: cartItem.quantity + 1 }
        : cartItem;
    });
  }
  return [...cartItems, { ...itemToadd, quantity: 1 }];
};

export const reduceItemQuantity = (cartItems, itemToReduce) => {
    const existingItem = cartItems.map(item => item.id == itemToReduce.id);

    if(existingItem.quantity == 1) {
        return cartItems.filter(item => cartItems.id !== itemToReduce.id);
    }

    return cartItems.map(item => 
        item.id == itemToReduce.id ? {...item, ...item.quantity - 1} : {...item}
    );
}
