import { React } from 'react';

import Spinner from '../spinner/spinner.component';

const withSpinner = withComponent => ({isLoading, ...otherProps}) => {
    return isLoading ? <Spinner />: <withComponent isLoading = {isLoading} {...otherProps} />
};

export default withSpinner;
