import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Route } from 'react-router-dom';

import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import CollectionPreview from '../../components/collection-preview/collection-preview.component';
import Collection from '../../components/collection/collection.component';

import { firestore, convertCollectionSnapshotToMap } from '../../firebase/firebase.util';
import { fetchCollectionStartAsync } from '../../redux/shop/shop.actions';

import withSpinner from '../../components/with-spinner/with-spinner.component';

const CollectionOverviewWithSpinner = withSpinner(CollectionOverview);
const CollectionWithSpinner = withSpinner(Collection);

class ShopPage extends Component {

    componentDidMount() {
       this.props.fetchCollections();
    }

    render() {
        return(
        <div className='shop-page'>
            <Route exact={true} path={`${this.props.match.path}`} render={ () => <CollectionOverviewWithSpinner isLoading={this.props.isLoading} {...this.props} />} />
            <Route path={`${this.props.match.path}/:collectionId`} render={() => <CollectionWithSpinner isLoading={this.props.isLoading} {...this.props} />} />
        </div>
        );
    }
}

const mapStateToProps = state => ({
    isLoading: state.shop.isLoading
});

const mapDispatchToProps = dispatch => ({
    fetchCollections: () => dispatch(fetchCollectionStartAsync())
});

export default connect(null, mapDispatchToProps)(ShopPage);
