import { createSelector } from 'reselect';
import { create } from 'istanbul-reports';

export const selectShop = state => state.shop;

export const selectShopCollections = createSelector(
    [selectShop],
    shop => shop.collections
);

export const selectCollectionSelector = collectionId => createSelector(
    [selectShopCollections],
    collections => {
        console.log('inside selector');
       console.log(collections[collectionId]);
        return (
            Object.keys(collections).map((collection) => (collection == collectionId) ? collections[collection]: '')
        );
    }
);
